import React from 'react';
import { Link } from 'react-router-dom'
import mockListings from "../assets/mocklisting";
// Import any additional assets or styles here

// The Listing component now accepts additional props for the host's name and bed type.
function ListingDetail({ imageUrl, hostName, description, bedType, price, onListingClick, title, id, room, size}) {
  // Function to handle when a listing is clicked
  const handleClick = () => {
    onListingClick();
  };

  // Render the listing item with additional details.
  return (
    <div className="detail-container" >
      <img src={imageUrl} alt={`Stay at ${hostName}`} className="listing-image" />
      <div className="listing-details">
        <h2>{hostName}</h2>
        <p>
        <span className='detail-description'>{description}</span>
        <span className='detail-bed'style={{ textAlign: 'right', display: 'block'}} >{bedType}</span>
        </p>
        <p className='detail-price'> {price}</p>
        <p className='detail-room'> {room}</p>
        <p className='detail-size'> {size}</p>
        {/* Add more details here if needed */}
      </div>
    </div>
  );
}

export default ListingDetail;
