    <div className="listing">
      <img src={imageUrl} alt={`Stay at ${hostName}`}
        className="listing-image"
        onClick={navigateToDetailPage} // Add click handler to image
      />
      <div className="listing-details">
        <h2 className="listing-host-name" onClick={navigateToDetailPage}>
          {hostName}
