import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Logo from '../assets/logo.png'; // Adjust the path if necessary
import "./Style.css"; // Ensure this contains the correct styles
import { navItems } from './NavItems';
import Button from './Button';
import Modal from './Modal'; 
function NavBar() {
  const [showSignInModal, setShowSignInModal] = useState(false);

  const handleSignInClick = () => {
    setShowSignInModal(true); // Show the sign-in modal
  };

  const handleCloseModal = () => {
    setShowSignInModal(false); // Hide the sign-in modal
  };
  return (
    <nav className="navbar">
      <Link to="/" className="navbar-logo">
        <img src={Logo} alt="Logo" height="50" />
      </Link>

      <ul className='nav-items'>
        {navItems.map((item) => {
          return (
            <li key={item.id} className={item.cName}>
              <Link to={item.path}>{item.title}</Link>
            </li>
          );
        })}
      </ul>
      {/* Updated Button to trigger the modal */}
      <button className='btn' onClick={handleSignInClick}>Sign In</button>

      {/* Sign In Modal */}
      <Modal show={showSignInModal} onClose={handleCloseModal}>
        {/* Your sign-in form or content goes here */}
        <p>Sign In Form</p>
      </Modal>
    </nav>
  );
}

export default NavBar;
